import styled from "styled-components";


const StyledBox = styled.div`
    background: gray;
    display: flex;
    flex-direction: column;




.selected{
    position: relative;
    display: flex;
    width: 250px;
    height: 50px;
    border: 2px red solid;
}

.box_btn{
    position: absolute;
    width: 50px;
    height: 50px;
    border: 2px yellow solid;
    right: 0px;
    display: flex;
    flex-direction: column;
    cursor:pointer;
}

.box_btn .bar{
    position: absolute;
    width: 20px;
    height: 4px;
    background: white;
    border-radius: 5px;
    top: 23px;
}

.box_btn .bar:nth-child(1){
    left: 7px;
    rotate: 45deg;
    transform: rotate(45deg)
}

.box_btn .bar:nth-child(2){
    left: 19px;
    rotate: 45deg;
    transform: rotate(-45deg)
}

.box_btn.is-active {
    background-color: purple;
}

.box_btn.is-active .bar:nth-child(1){
    left: 7px;
    rotate: 45deg;
    transform: rotate(45deg)
}

.box_btn.is-active .bar:nth-child(2){
    left: 19px;
    rotate: 45deg;
    transform: rotate(-45deg)
}

.option_container{
    display: flex;
    flex-direction: column;
}

.option{
    padding: 5px;
    cursor: pointer;
}

.option:hover{
    background-color: pink;
}

label{
    cursor: pointer;
}

input{
    display: none;
}



`

const SelectBox = () => {

    const boxBtn = document.querySelector(".box_btn");

    boxBtn.addEventListener("click", () => {
        boxBtn.classList.toggle('is-active');
    })

    const Trigger = (e) => {
        console.log('click');
        const btnBar = e.target;
        btnBar.classList.toggle('is-active');
        //menu.classList.toggle("is-active");
    }

    return(
        <StyledBox>
            <div className = "selected">
                Select a sorting algorithm
                <div className = "box_btn" onClick={Trigger}>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </div>

            <div className="option_container">
                <div className="option">
                <input type="radio" id="bubble" name="algorighm"></input>
                    <label>Bubble Sort</label>
                </div>

                <div className="option">
                <input type="radio" id="selection" name="algorighm"></input>
                    <label>Selection sort</label>
                </div>
            </div>

        </StyledBox>
    )

}


export default SelectBox;
