import React, { Component } from "react";
import { Section, Header } from "./Sorting.styled";
import ArrayBar from './ArrayBar'
import { BubbleSort } from "./SortingAlgorithm";


class Sorting extends Component{

    state = {
        array: [],
        steps: [],
        color: [],
        timouts: [],
        count: 10,
        delay: 300,
        algorithm: '',
        currentStep: 0
    }

    componentDidMount() {
        this.GenerateArray();
    }

    GenerateArray = () => {
        let arr = [];
        for(let i=0; i<this.state.count; i++){
            arr.push(Math.floor(Math.random() * 1000))
        }

        this.setState({
            array: arr,
            steps: [arr]
        })       
    }

    ChangeArray = (index, value) => {
        let array = this.state.array;
        array[index] = value
        this.setState({
            array: array,
            steps: [array]
        })
    }

    SortArray = () => {
        let steps = this.state.steps;
        BubbleSort(this.state.array, steps);
        this.setState({
            steps: steps,
        })
        console.log(this.state.steps.length)
    }

    PlayAround = () => {    

        for(let i=0; i<this.state.steps.length; i++){
            setTimeout(() => {
                let newArray = this.state.steps[i]
                this.setState({
                    array: newArray
                })
            }, this.state.delay * i)
        }
    }


    render() {
        return(
            <div>
                <Header className='functionBar'>
                    <button onClick={this.GenerateArray}>reset arrays</button>
                    <button onClick={this.SortArray}>Bubble Sort</button>
                    <button onClick={this.PlayAround}>Play around</button>
                </Header>
                <Section>
                    {this.state.array.map((item, index) => 
                    <ArrayBar key={index} index={index} length={item} changeArray={this.ChangeArray}/>)}
                </Section>
            </div>
        )
    }
}

export default Sorting;


