import styled from "styled-components";


const StyledBox = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;



.selected{
    background-color: gray;
    position: relative;
    display: flex;
    width: 250px;
    height: 50px;
    align-items: center;
}

.box_btn{
    position: absolute;
    width: 50px;
    height: 50px;
    right: 0px;
    display: flex;
    flex-direction: column;
    cursor:pointer;
}

.box_btn .bar{
    position: absolute;
    width: 20px;
    height: 4px;
    background: white;
    border-radius: 5px;
    top: 23px;
}

.box_btn .bar:nth-child(1){
    left: 7px;
    rotate: 45deg;
    transform: rotate(45deg);
    transition: 0.3s;
}

.box_btn .bar:nth-child(2){
    left: 19px;
    rotate: 45deg;
    transform: rotate(-45deg);
    transition: 0.3s;
}

.box_btn.is-active .bar:nth-child(1){
    left: 19px;
    rotate: 45deg;
    transform: rotate(45deg);
    transition: 0.3s;
}

.box_btn.is-active .bar:nth-child(2){
    left: 7px;
    rotate: 45deg;
    transform: rotate(-45deg);
    transition: 0.3s;
}

.option_container{
    position: relative;
    display: flex;
    flex-direction: column;
    background-color: gray;
    top: -300px;
    opacity: 0;
    transition:all, 0.5s;
}

.option_container.active{
    top: 0px;
    opacity: 0.7;
    transition:all, 0.5s;
}

.option{
    height: 50px;
    cursor: pointer;
}

.option:hover{
    background-color: pink;
}

label{
    padding: 10px;
    border: 2px yellow solid;
    display: flex;
    align-items: center;
    width: 100%;
    height: 100%;
    cursor: pointer;
}

input{
    display: none;
}
`

const SelectBox = () => {


    const Trigger = (e) => {
        const btn = document.querySelector(".box_btn");
        const container = document.querySelector(".option_container");
        btn.classList.toggle('is-active');
        container.classList.toggle("active");
    }

    return(
        <StyledBox>
            <div className = "selected">
                Select a sorting algorithm
                <div className = "box_btn" onClick={Trigger}>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </div>

            <div className="option_container">
                <div className="option">
                <input type="radio" id="bubble" name="algorighm"></input>
                    <label for="bubble">Bubble Sort</label>
                </div>

                <div className="option">
                <input type="radio" id="selection" name="algorighm"></input>
                    <label for="selection">Selection sort</label>
                </div>
            </div>

        </StyledBox>
    )

}


export default SelectBox;
