import React, { Component, useCallback } from "react";
import { Section, Header } from "./Sorting.styled";
import ArrayBar from './ArrayBar'
import { BubbleSort } from "./SortingAlgorithm";


class Sorting extends Component{

    state = {
        array: [],
        steps: [],
        colorKeys: [],
        colorSteps: [],
        //timeouts: [],
        currentStep: 0,
        count: 20,
        delay: 30,
        algorithm: '',
        working: false
    }

    //



	componentDidMount() {
		this.generateElements();
	}

	handleStart = () => {

        this.SortArray(); 

        let i=0;
            while(i<this.state.steps.length){

                setTimeout(() => {
                    let currentStep = this.state.currentStep;
                    console.log(currentStep);
                    this.setState({
                        array: this.state.steps[currentStep],
                        colorKeys: this.state.colorSteps[currentStep],
                        currentStep: currentStep+1
                    }) 
                }, i*this.state.delay);
                i++;
            }
        /*
        this.setState({}, () => {
            let i=0;
            while(i<this.state.steps.length){

                setTimeout(() => {
                    let currentStep = this.state.currentStep;
                    console.log(currentStep);
                    this.setState({
                        array: this.state.steps[currentStep],
                        colorKeys: this.state.colorSteps[currentStep],
                        currentStep: currentStep+1
                    }) 
                }, i*this.state.delay);
                i++;
            }
        })
        */

	};


	generateRandomNumber = (min, max) => {
		return Math.floor(Math.random() * (max - min) + min);
	};


    generateElements = () => {
        let count = this.state.count;
        let arr = [];
        let colorKeys = new Array(count).fill(0);

        for (let i = 0; i < count; i++) {
            arr.push(this.generateRandomNumber(50, 200));
        }

        this.setState(
            {
                array: arr,
                steps: [arr],
                colorKeys: colorKeys,
                colorSteps: [colorKeys]
            },
        );



        console.log(arr);
    };

    SortArray = () => {
        let array = this.state.array.slice();
        let colorKeys = new Array(this.state.count).fill(0);
        let steps = [array];
        let colorSteps = [colorKeys];
        BubbleSort(array, colorKeys , steps, colorSteps);
        this.setState({
            currentStep: 0,
            steps: steps,
            colorSteps: colorSteps    
        })
    }




    Printout = () => {
        console.log(this.state.array);
        this.clearState();
    }

    render() {
        const Bars = this.state.array.map((item, index) => {
            return(
            <ArrayBar key={index} index={index} length={item} colorKey={this.state.colorKeys[index]}></ArrayBar>
            );
        });
        return(
            <div>
                <Header className='functionBar'>
                    <button onClick={this.generateElements}>reset arrays</button>
                    <button onClick={this.handleStart}>Bubble Sort</button>
                    <button onClick={this.Printout}>Bubble Sort</button>
                </Header>
                <Section>
                    {Bars}
                </Section>
            </div>
        )
    }
}

export default Sorting;
