export const BubbleSort = (array, steps, color) =>{



    var newArray = [...array];
    var total = 0;

    for(let i=0; i<newArray.length; i++){
        for(let j=0; j<newArray.length-1; j++){
            if(newArray[j] > newArray[j+1]){
                let temp = newArray[j];
                newArray[j] = newArray[j+1];
                newArray[j+1] = temp;
            }
            total++;
            steps.push([...newArray]);
        }
    }

}
