import styled from "styled-components";


const StyledBox = styled.div`
    display: flex;
    flex-direction: column;
    position: relative;

.selected{
    padding-left: 10px;
    color: white;
    background-color: #2f3640;
    position: relative;
    display: flex;
    width: 400px;
    height: 50px;
    align-items: center;
    border-radius: 8px;
}

.box_btn{
    position: absolute;
    width: 50px;
    height: 50px;
    right: 0px;
    display: flex;
    flex-direction: column;
    cursor:pointer;
}

.box_btn .bar{
    position: absolute;
    width: 20px;
    height: 4px;
    background: white;
    border-radius: 5px;
    top: 23px;
}

.box_btn .bar:nth-child(1){
    left: 7px;
    rotate: 45deg;
    transform: rotate(45deg);
    transition: 0.3s;
}

.box_btn .bar:nth-child(2){
    left: 19px;
    rotate: 45deg;
    transform: rotate(-45deg);
    transition: 0.3s;
}

.box_btn.is-active .bar:nth-child(1){
    left: 19px;
    rotate: 45deg;
    transform: rotate(45deg);
    transition: 0.3s;
}

.box_btn.is-active .bar:nth-child(2){
    left: 7px;
    rotate: 45deg;
    transform: rotate(-45deg);
    transition: 0.3s;
}


.option_container{
    position: relative;
    display: flex;
    flex-direction: column;
    background-color: #2f3640;
    border-radius: 8px;
    overflow: hidden;
    top: -300px;
    opacity: 0;
    transition:all, 0.5s;
}

.option_container.active{
    top: 10px;
    opacity: 0.8;
    transition:all, 0.5s;
}

.option{
    height: 50px;
    cursor: pointer;
}

.option:hover{
    background-color: pink;
}

label{
    color: white;
    padding-left: 10px;
    display: flex;
    align-items: center;
    width: 100%;
    height: 100%;
    cursor: pointer;
}

input{
    display: none;
}
`

const SelectBox = () => {

    const algorithms = ["bubble", "selection"];

    const Trigger = (e) => {
        const btn = document.querySelector(".box_btn");
        const container = document.querySelector(".option_container");
        btn.classList.toggle('is-active');
        container.classList.toggle("active");
    }

    const Selecting = (item) => {

        console.log(item);
    }

    return(
        <StyledBox>
            <div className = "selected">
                Select a sorting algorithm
                <div className = "box_btn" onClick={Trigger}>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </div>

            <div className="option_container">

                {algorithms.map((item, index) => (
                    <div className="option" key={index}>
                    <input type="radio" id={item} name="algorighm" onClick={() => Selecting({item})}></input>
                        <label htmlFor={item}>{item}</label>
                    </div>
                ))}
            </div>

        </StyledBox>
    )

}


export default SelectBox;
