export const BubbleSort = (array, ChangeArray) =>{

    const delay = 1;

    var newArray = [...array];

    for(let i=0; i<newArray.length; i++){
        for(let j=0; j<newArray.length-1; ){
            if(newArray[j] > newArray[j+1]){
                let temp = newArray[j];
                newArray[j] = newArray[j+1];
                newArray[j+1] = temp;
                ChangeArray(j, newArray[j+1]);
                ChangeArray(j+1, temp);

                setTimeout(() => {
                    j++
                }, 300)
            }
        }
    }
}
