export const BubbleSort = (array, colorKeys, steps, colorSteps) =>{

    for(let i=0; i<array.length; i++){
        for(let j=0; j<array.length-1-i; j++){
            if(array[j] > array[j+1]){
                let temp = array[j];
                array[j] = array[j+1];
                array[j+1] = temp;
            }
            colorKeys[j] = 1;
            colorKeys[j+1] = 1;

            steps.push([...array]);
            colorSteps.push([...colorKeys]);

            colorKeys[j] = 0;
            colorKeys[j+1] = 0;
        }
        steps.push([...array]);
        colorKeys[array.length-1-i] = 2;
    }
    steps.push([...array]);
    colorSteps.push([...colorKeys]);

}


export const SelectionSort = (array, colorKeys, steps, colorSteps) => {
    for(let i=0; i<array.length; i++){
        let min = i;
        colorKeys[i] = 1;
        for(let j=i+1; j<array.length; j++){
            if(array[j] < array[min]){
                colorKeys[min] = 0;
                min = j;
                colorKeys[min] = 1;
                colorKeys[i] = 1;
            }
            colorKeys[j] = 1;
            colorKeys[j] = 0;

            colorSteps.push([...colorKeys]);
            steps.push([...array]);

        }
        let swap = array[i];
        array[i] = array[min];
        array[min] = swap;

        colorKeys[i] = 2;
        colorKeys[min] = 0;

        colorSteps.push([...colorKeys]);
        steps.push([...array]);

    }
}
