import styled from "styled-components";

const StyledBox = styled.div`
    background: gray;
    display: flex;
    flex-direction: column;


.selected{
    position: relative;
    display: flex;
    width: 250px;
    height: 50px;
    border: 2px red solid;
}

.box_btn{
    position: absolute;
    width: 50px;
    height: 50px;
    border: 2px yellow solid;
    right: 0px;
    display: flex;
    flex-direction: column;
}

.box_btn .bar{

}

.option_container{
    display: flex;
    flex-direction: column;
}

.option{
    padding: 5px;
    cursor: pointer;
}

.option:hover{
    background-color: pink;
}

label{
    cursor: pointer;
}

input{
    display: none;
}



`

const SelectBox = () => {

    return(
        <StyledBox>
            <div className = "selected">
                Select a sorting algorithm
                <div className = "box_btn">
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </div>

            <div className="option_container">
                <div className="option">
                <input type="radio" id="bubble" name="algorighm"></input>
                    <label>Bubble Sort</label>
                </div>

                <div className="option">
                <input type="radio" id="selection" name="algorighm"></input>
                    <label>Selection sort</label>
                </div>
            </div>
        </StyledBox>
    )
}


export default SelectBox;
