import { useEffect, useState } from 'react'
import styled from 'styled-components'



const StyledBar =  styled.div`
    display: flex;
    margin: 5px 5px;
    width: 10px;
    height: 10px;

    &.0{

    }
`

const ArrayBar = ({index, length, colorKey}) => {
    const [len, setLen] = useState(length);

    const colors = ['skyblue', 'pink', '#7fffd4'];

    useEffect(() => {
        setLen(length);
    }, [length])

    return(
        <div>
            <StyledBar className={colors[colorKey]} style={{width: len, background: colors[colorKey]}}></StyledBar>
        </div>
    )
}

export default ArrayBar;
