export const BubbleSort = (array, steps) =>{



    var newArray = [...array];
    var i =0;

    console.log(newArray.length);
    for(let i=0; i<newArray.length; i++){
        for(let j=0; j<newArray.length-1; j++){
            if(newArray[j] > newArray[j+1]){
                let temp = newArray[j];
                newArray[j] = newArray[j+1];
                newArray[j+1] = temp;
            }
            steps.push(...newArray);
        }
    }
}
