import React, { Component } from "react";
import { Section, Header } from "./Sorting.styled";
import ArrayBar from './ArrayBar'
import { BubbleSort } from "./SortingAlgorithm";


class Sorting extends Component{

    state = {
        array: [],
        steps: [],
        color: [],
        timeouts: [],
        count: 10,
        delay: 300,
        algorithm: '',
        currentStep: 0
    }

    componentDidMount() {
        this.GenerateArray();
    }

    GenerateArray = () => {
        this.ClearTimeouts();

        let count = this.state.count;
        let arr = [];

        for(let i=0; i<this.state.count; i++){
            arr.push(Math.floor(Math.random() * 1000))
        }

        this.setState(
            {
                array: arr,
                steps: [arr],
                count: count,
                currentStep: 0,
            }
        );

        console.log(arr);
    }

    ChangeArray = (index, value) => {
        let array = this.state.array;
        array[index] = value
        this.setState({
            array: array,
            steps: [array]
        })
    }

    SortArray = () => {
        let steps = [];

        BubbleSort(this.state.array, steps);
        this.setState({
            steps: steps,
        })
    }

    ClearTimeouts = () => {
		this.setState({ timeouts: [] });
    }

    PlayAround = () => {    

        let steps = this.state.steps;
        this.ClearTimeouts();
		let timeouts = [];

		let i = 0;
		while (i < steps.length - this.state.currentStep) {
			let timeout = setTimeout(() => {
				let currentStep = this.state.currentStep;
				this.setState({
					array: steps[currentStep],
					currentStep: currentStep + 1,
				});
				timeouts.push(timeout);
			}, this.state.delay * i);
			i++;
		}

		this.setState({
			timeouts: timeouts,
		});
    }


    render() {
        return(
            <div>
                <Header className='functionBar'>
                    <button onClick={this.GenerateArray}>reset arrays</button>
                    <button onClick={this.SortArray}>Bubble Sort</button>
                    <button onClick={this.PlayAround}>Play around</button>
                </Header>
                <Section>
                    {this.state.array.map((item, index) => 
                    <ArrayBar key={index} index={index} length={item} changeArray={this.ChangeArray}/>)}
                </Section>
            </div>
        )
    }
}

export default Sorting;
