import styled from "styled-components"


export const Header = styled.div`
    height: 100px;
    width: 100%;
    display: flex;
    justify-content: center;
    align-items: center;


button{
    height: 50px;
}

button:hover{
    background-color: pink;
}

.select_box{
    height: 500px;
    background: gray;
    display: flex;
    flex-direction: column;
}

`



export const Section = styled.div`
    display: flex;
    justify-content: center;

`
