import React, { Component } from "react";
import { Section, Header } from "./Sorting.styled";
import ArrayBar from './ArrayBar'
import { BubbleSort } from "./SortingAlgorithm";


class Sorting extends Component{

    state = {
        array: [],
        steps: [],
        color: [],
        timeouts: [],
        count: 20,
        delay: 300,
        algorithm: '',
        currentStep: 0
    }


	componentDidMount() {
		this.generateElements();
	}

	handleStart = () => {
		let steps = this.state.steps;

		this.clearTimeouts();
		let timeouts = [];

		let i = 0;
		while (i < steps.length - this.state.currentStep) {
			let timeout = setTimeout(() => {
				let currentStep = this.state.currentStep;
				this.setState({
					array: steps[currentStep],
					currentStep: currentStep + 1,
				});
				timeouts.push(timeout);
			}, this.state.delay * i);
			i++;
		}

		this.setState({
			timeouts: timeouts,
		});
	};

	generateSteps = () => {
		let array = this.state.array.slice();
		let steps = this.state.steps.slice();

		BubbleSort(array, steps);
		this.setState({
			steps: steps,
		});
	};

	generateRandomNumber = (min, max) => {
		return Math.floor(Math.random() * (max - min) + min);
	};

	clearTimeouts = () => {
		this.state.timeouts.forEach((timeout) => clearTimeout(timeout));
		this.setState({ timeouts: [] });
	};

    generateElements = () => {
        this.clearTimeouts();
        this.clearColorKey()

            let count = this.state.count;
            let arr = [];

            for (let i = 0; i < count; i++) {
                arr.push(this.generateRandomNumber(50, 200));
            }

            this.setState(
                {
                    array: arr,
                    steps: [arr],
                    count: count,
                    currentStep: 0,
                },
                () => this.generateSteps()
            );

            console.log(arr);
        };

        changeArray = (index, value) => {
            let array = this.state.array;
            array[index] = value;
            this.setState({
                array: array,
                steps: [array],
                currentStep: 0,
            }, () => this.generateSteps());

        };


    SortArray = () => {
        let steps = [];

        BubbleSort(this.state.array, steps);
        this.setState({
            steps: steps,
        })
    }

    ClearTimeouts = () => {
		this.state.timeouts.forEach((timeout) => clearTimeout(timeout));
		this.setState({ timeouts: [] });
    }




    render() {
        const Bars = this.state.array.map((item, index) => {
            return(
            <ArrayBar key={index} index={index} length={item}/>
            );
        });
        return(
            <div>
                <Header className='functionBar'>
                    <button onClick={this.GenerateArray}>reset arrays</button>
                    <button onClick={this.SortArray}>Bubble Sort</button>
                    <button onClick={this.PlayAround}>Play around</button>
                </Header>
                <Section>
                    {Bars}
                </Section>
            </div>
        )
    }
}

export default Sorting;
